// frontend/src/components/common/ClientSearchBar.js
import React, { useState, useEffect } from 'react';
import { searchClientsByFace } from '../../services/api';

const ClientSearchBar = ({ onSearch, clients }) => { // Ajout du prop clients
  const [searchParams, setSearchParams] = useState({
      imageFace: null,
      firstName: '',
      lastName: '',
      ubi: ''
  });
  const [previewImage, setPreviewImage] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageChange = async (file) => {
    if (file) {
        try {
            setLoading(true);
            setPreviewImage(URL.createObjectURL(file));
            setSearchParams(prev => ({ ...prev, imageFace: file }));

            // Vérifier que nous avons des clients avec des images
            const clientsWithImages = clients.filter(client => client.imageFace);
            if (clientsWithImages.length === 0) {
                throw new Error("Aucun client avec photo à comparer");
            }

            const similarClients = await searchClientsByFace(file, clientsWithImages);
            onSearch({ 
                type: 'FACE',
                result: similarClients,
                searchType: 'Face Recognition'
            });
        } catch (error) {
            console.error('Erreur reconnaissance faciale:', error);
            onSearch({ 
                type: 'FACE', 
                result: null, 
                error: error.message,
                searchType: 'Face Recognition'
            });
        } finally {
            setLoading(false);
        }
    }
  };

  const handleTextChange = (e) => {
    const { name, value } = e.target;
    setSearchParams(prev => ({ ...prev, [name]: value }));
  };

  useEffect(() => {
    const debounceTimeout = setTimeout(() => {
      onSearch({
        type: 'TEXT',
        params: {
          firstName: searchParams.firstName,
          lastName: searchParams.lastName,
          ubi: searchParams.ubi
        }
      });
    }, 300);

    return () => clearTimeout(debounceTimeout);
  }, [searchParams.firstName, searchParams.lastName, searchParams.ubi, onSearch]);

  return (
    <div className="mb-6 bg-white p-4 rounded-lg shadow">
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Rechercher par image
          </label>
          <div className="flex items-center">
            {previewImage && (
              <img
                src={previewImage}
                alt="Preview"
                className="h-12 w-12 rounded-full mr-4 object-cover"
              />
            )}
            <input
              type="file"
              accept="image/*"
              onChange={(e) => handleImageChange(e.target.files[0])}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
              disabled={loading}
            />
          </div>
          {loading && (
            <div className="mt-2 text-sm text-gray-500">
              Comparaison en cours...
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            UBI
          </label>
          <input
            type="text"
            name="ubi"
            value={searchParams.ubi}
            onChange={handleTextChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
            placeholder="Numéro UBI"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Nom
          </label>
          <input
            type="text"
            name="lastName"
            value={searchParams.lastName}
            onChange={handleTextChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
            placeholder="Nom de famille"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Prénom
          </label>
          <input
            type="text"
            name="firstName"
            value={searchParams.firstName}
            onChange={handleTextChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-md"
            placeholder="Prénom"
          />
        </div>
      </div>
    </div>
  );
};

export default ClientSearchBar;
