// frontend/src/components/ClientForm.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { createClient, updateClient, getClient } from '../services/api';

// Composant pour l'affichage des similitudes
const SimilarityAlert = ({ similarities, onClose }) => {
    if (!similarities || !similarities.similarClients || similarities.similarClients.length === 0) {
        return null;
    }

    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleDateString('fr-FR');
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
            <div className="bg-white rounded-lg max-w-4xl w-full max-h-[80vh] overflow-hidden">
                <div className="px-6 py-4 bg-red-600 text-white flex justify-between items-center">
                    <h3 className="text-xl font-semibold">⚠️ Similitudes détectées</h3>
                    <button onClick={onClose} className="text-white hover:text-gray-200 text-2xl font-bold">×</button>
                </div>
                <div className="p-6 overflow-auto">
                    <div className="mb-4">
                        <h4 className="text-lg font-semibold mb-2">Client que vous essayez de créer :</h4>
                        <div className="bg-gray-50 p-4 rounded-lg">
                            <p><span className="font-semibold">Nom :</span> {similarities.potentialClient.lastName}</p>
                            <p><span className="font-semibold">Prénom :</span> {similarities.potentialClient.firstName}</p>
                            <p><span className="font-semibold">Date de naissance :</span> {formatDate(similarities.potentialClient.dateOfBirth)}</p>
                            <p><span className="font-semibold">Email :</span> {similarities.potentialClient.email}</p>
                            <p><span className="font-semibold">Nationalités :</span> {similarities.potentialClient.nationalities.join(', ')}</p>
                        </div>
                    </div>

                    <h4 className="text-lg font-semibold mb-2">Clients similaires trouvés :</h4>
                    <div className="space-y-4">
                        {similarities.similarClients.map((client, index) => (
                            <div key={index} className="bg-yellow-50 p-4 rounded-lg border border-yellow-200">
                                <div className="flex justify-between items-start mb-2">
                                    <div>
                                        <p className="font-bold text-lg">{client.firstName} {client.lastName}</p>
                                        <p className="text-sm text-gray-600">UBI: {client.UBI}</p>
                                    </div>
                                    <div className="bg-yellow-100 px-3 py-1 rounded-full">
                                        <span className="text-yellow-800">
                                            Score de similarité: {client.matchScore}%
                                        </span>
                                    </div>
                                </div>
                                <div className="grid grid-cols-2 gap-4 mt-2">
                                    <div>
                                        <p><span className="font-semibold">Date de naissance:</span> {formatDate(client.dateOfBirth)}</p>
                                        <p><span className="font-semibold">Email:</span> {client.email}</p>
                                    </div>
                                    <div>
                                        {client.matchDetails && (
                                            <div>
                                                <p className="font-semibold text-yellow-700">Détails des correspondances:</p>
                                                <ul className="list-disc list-inside text-sm">
                                                    {client.matchDetails.map((detail, idx) => (
                                                        <li key={idx}>{detail}</li>
                                                    ))}
                                                </ul>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>

                    <div className="mt-6 flex justify-end space-x-4">
                        <button
                            onClick={onClose}
                            className="px-4 py-2 bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200"
                        >
                            Modifier les informations
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

// Composant pour le téléchargement d'image
const ImageUpload = ({ value, onChange }) => {
    const handleImageChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            if (!['image/jpeg', 'image/png', 'image/jpg'].includes(file.type)) {
                alert('Format d\'image invalide. Utilisez JPEG, PNG ou JPG.');
                return;
            }

            if (file.size > 2 * 1024 * 1024) {
                alert('L\'image est trop grande. Taille maximale: 2MB');
                return;
            }

            const reader = new FileReader();
            reader.onload = (e) => {
                onChange(e.target.result);
            };
            reader.readAsDataURL(file);
        }
    };

    return (
        <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">
                Document d'identification
            </label>
            <input
                type="file"
                accept="image/jpeg,image/png,image/jpg"
                onChange={handleImageChange}
                className="mt-1 block w-full text-sm text-gray-500
                    file:mr-4 file:py-2 file:px-4
                    file:rounded-md file:border-0
                    file:text-sm file:font-semibold
                    file:bg-blue-50 file:text-blue-700
                    hover:file:bg-blue-100"
            />
            {value && (
                <div className="mt-2">
                    <img
                        src={value}
                        alt="Aperçu du document"
                        className="h-32 object-cover rounded"
                    />
                </div>
            )}
            <p className="text-sm text-gray-500 mt-1">
                Formats acceptés: JPEG, PNG, JPG. Taille maximale: 2MB
            </p>
        </div>
    );
};

// Composant principal du formulaire
function ClientForm() {
    const { ubi } = useParams();
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [similarities, setSimilarities] = useState(null);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        email: '',
        accountList: [],
        nationalities: [],
        imageDocumentIdentification: ''
    });

    const UEMOA_COUNTRIES = [
        { code: 'BEN', name: 'Bénin' },
        { code: 'BFA', name: 'Burkina Faso' },
        { code: 'CIV', name: 'Côte d\'Ivoire' },
        { code: 'GNB', name: 'Guinée-Bissau' },
        { code: 'MLI', name: 'Mali' },
        { code: 'NER', name: 'Niger' },
        { code: 'SEN', name: 'Sénégal' },
        { code: 'TGO', name: 'Togo' }
    ];

    useEffect(() => {
        const fetchClient = async () => {
            if (ubi) {
                try {
                    setLoading(true);
                    const data = await getClient(ubi);
                    setFormData(data);
                } catch (err) {
                    setError("Erreur lors du chargement du client");
                } finally {
                    setLoading(false);
                }
            }
        };

        fetchClient();
    }, [ubi]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleNationalityChange = (e) => {
        const value = Array.from(e.target.selectedOptions, option => option.value);
        setFormData(prev => ({ ...prev, nationalities: value }));
    };

    const handleImageChange = (imageData) => {
        setFormData(prev => ({ ...prev, imageDocumentIdentification: imageData }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            setError(null);
            setSimilarities(null);

            const submitData = {
                ...formData,
                accountList: JSON.stringify([]),  // Liste vide pour la création
                nationalities: JSON.stringify(formData.nationalities)
            };

            if (ubi) {
                await updateClient(ubi, submitData);
                navigate('/');
            } else {
                const response = await createClient(submitData);
                const result = JSON.parse(response);

                if (result.similitude) {
                    setSimilarities(result);
                } else {
                    navigate('/');
                }
            }
        } catch (err) {
            if (err.response?.data?.similitude) {
                setSimilarities(err.response.data);
            } else {
                setError(err.message);
            }
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="flex justify-center items-center h-64">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-bceao-primary"></div>
            </div>
        );
    }

    return (
        <div className="container mx-auto px-4 py-8">
            {similarities && (
                <SimilarityAlert
                    similarities={similarities}
                    onClose={() => setSimilarities(null)}
                />
            )}

            <div className="max-w-2xl mx-auto bg-white shadow-lg rounded-lg overflow-hidden">
                <div className="px-6 py-4 bg-bceao-primary text-white">
                    <h2 className="text-xl font-bold">
                        {ubi ? 'Modifier le Client' : 'Nouveau Client'}
                    </h2>
                </div>

                {error && (
                    <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                        {error}
                    </div>
                )}

                <form onSubmit={handleSubmit} className="p-6 space-y-6">
                    <div className="grid grid-cols-2 gap-6">
                        <div>
                            <label className="block text-sm font-medium text-gray-700">
                                Prénom
                            </label>
                            <input
                                type="text"
                                name="firstName"
                                value={formData.firstName}
                                onChange={handleChange}
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-bceao-primary focus:ring-bceao-primary"
                                required
                            />
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700">
                                Nom
                            </label>
                            <input
                                type="text"
                                name="lastName"
                                value={formData.lastName}
                                onChange={handleChange}
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-bceao-primary focus:ring-bceao-primary"
                                required
                            />
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700">
                                Email
                            </label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-bceao-primary focus:ring-bceao-primary"
                                required
                            />
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700">
                                Date de Naissance
                            </label>
                            <input
                                type="date"
                                name="dateOfBirth"
                                value={formData.dateOfBirth}
                                onChange={handleChange}
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-bceao-primary focus:ring-bceao-primary"
                                required
                            />
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700">
                                Genre
                            </label>
                            <select
                                name="gender"
                                value={formData.gender}
                                onChange={handleChange}
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-bceao-primary focus:ring-bceao-primary"
                                required
                            >
                                <option value="">Sélectionner</option>
                                <option value="M">Masculin</option>
                                <option value="F">Féminin</option>
                            </select>
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700">
                                Nationalités
                            </label>
                            <select
                                multiple
                                name="nationalities"
                                value={formData.nationalities}
                                onChange={handleNationalityChange}
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-bceao-primary focus:ring-bceao-primary"
                                required
                            >
                                {UEMOA_COUNTRIES.map(country => (
                                    <option key={country.code} value={country.code}>
                                        {country.name}
                                    </option>
                                ))}
                            </select>
                            <p className="text-sm text-gray-500 mt-1">
                                Maintenez Ctrl pour sélectionner plusieurs nationalités
                            </p>
                        </div>
                    </div>

                    <ImageUpload
                        value={formData.imageDocumentIdentification}
                        onChange={handleImageChange}
                    />

                    <div className="flex justify-end space-x-3 pt-4">
                        <button
                            type="button"
                            onClick={() => navigate('/')}
                            className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
                        >
                            Annuler
                        </button>
                        <button
                            type="submit"
                            disabled={loading}
                            className={`px-4 py-2 rounded-md text-white
                                ${loading ? 'bg-gray-400' : 'bg-bceao-primary hover:bg-blue-700'}
                                transition-colors duration-200`}
                        >
                            {loading ? (
                                <div className="flex items-center">
                                    <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                    </svg>
                                    Enregistrement...
                                </div>
                            ) : 'Enregistrer'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default ClientForm;
