import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { getClient, updateClient } from '../services/api';
import BankAccountManagement from './BankAccountManagement';
import NationalityManagement from './NationalitiesManagement';
import ImagePreview from './common/ImagePreview';

const INITIAL_CLIENT_STATE = {
  UBI: '',
  firstName: '',
  lastName: '',
  dateOfBirth: '',
  gender: '',
  email: '',
  imageDocumentIdentification: '',
  nationalities: [],
  accountList: [],
};

const GENDER_OPTIONS = [
  { value: '', label: 'Sélectionner un genre' },
  { value: 'M', label: 'Masculin' },
  { value: 'F', label: 'Féminin' },
];

function UpdateClient() {
  const { ubi } = useParams();
  const navigate = useNavigate();
  const [client, setClient] = useState(INITIAL_CLIENT_STATE);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    const fetchClient = async () => {
      try {
        setLoading(true);
        setError(null);
        const data = await getClient(ubi);
        if (!data) {
          throw new Error('Client non trouvé');
        }
        setClient(data);
      } catch (err) {
        setError(err.message || 'Erreur lors du chargement du client');
      } finally {
        setLoading(false);
      }
    };

    if (ubi) {
      fetchClient();
    }
  }, [ubi]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setClient((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleNationalitiesChange = (newNationalities) => {
    setClient((prev) => ({
      ...prev,
      nationalities: newNationalities
    }));
  };

  const handleAccountsChange = (newAccounts) => {
    setClient((prev) => ({
      ...prev,
      accountList: newAccounts
    }));
  };

  const handleUpdateClient = async (e) => {
    e.preventDefault();
    if (isSubmitting) return;

    try {
      setIsSubmitting(true);
      setError(null);

      const updatedData = {
        firstName: client.firstName.trim(),
        lastName: client.lastName.trim(),
        dateOfBirth: client.dateOfBirth,
        gender: client.gender,
        email: client.email.trim(),
        imageDocumentIdentification: client.imageDocumentIdentification
      };

      const updatedClient = await updateClient(ubi, updatedData);
      if (!updatedClient) {
        throw new Error('Erreur lors de la mise à jour du client');
      }

      navigate(`/clients/${ubi}`);
    } catch (err) {
      setError(err.message || 'Une erreur est survenue lors de la mise à jour');
      window.scrollTo({ top: 0, behavior: 'smooth' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const renderFormField = ({ id, label, type = 'text', value, required = true, options = null }) => (
    <div>
      <label htmlFor={id} className="block text-sm font-medium text-gray-700 mb-2">
        {label}
      </label>
      {options ? (
        <select
          id={id}
          name={id}
          value={value}
          onChange={handleInputChange}
          className="w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          required={required}
        >
          {options.map((option) => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      ) : (
        <input
          type={type}
          id={id}
          name={id}
          value={value}
          onChange={handleInputChange}
          className="w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          required={required}
        />
      )}
    </div>
  );

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-bceao-primary"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-white shadow-lg rounded-lg overflow-hidden">
        <div className="bg-bceao-primary text-white px-6 py-4">
          <h2 className="text-2xl font-bold">Modifier les informations du client</h2>
        </div>

        {error && (
          <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mx-6 mt-6">
            {error}
          </div>
        )}

        <div className="p-6">
          <form onSubmit={handleUpdateClient} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {renderFormField({
                id: 'lastName',
                label: 'Nom',
                value: client.lastName
              })}
              {renderFormField({
                id: 'firstName',
                label: 'Prénom',
                value: client.firstName
              })}
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {renderFormField({
                id: 'dateOfBirth',
                label: 'Date de naissance',
                type: 'date',
                value: client.dateOfBirth
              })}
              {renderFormField({
                id: 'gender',
                label: 'Genre',
                value: client.gender,
                options: GENDER_OPTIONS
              })}
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {renderFormField({
                id: 'email',
                label: 'Email',
                type: 'email',
                value: client.email
              })}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Document d'identification
                </label>
                {client.imageDocumentIdentification ? (
                  <ImagePreview imageData={client.imageDocumentIdentification} />
                ) : (
                  <div className="h-40 bg-gray-100 rounded-lg flex items-center justify-center">
                    <p className="text-gray-500">Aucun document</p>
                  </div>
                )}
              </div>
            </div>

            <div className="border-t border-gray-200 pt-6">
              <NationalityManagement
                clientUBI={client.UBI}
                nationalities={client.nationalities}
                onNationalitiesChange={handleNationalitiesChange}
              />
            </div>

            <div className="border-t border-gray-200 pt-6">
              <BankAccountManagement
                clientUBI={client.UBI}
                accounts={client.accountList}
                onAccountsChange={handleAccountsChange}
              />
            </div>

            <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200">
              <Link
                to={`/clients/${ubi}`}
                className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50 transition-colors"
              >
                Annuler
              </Link>
              <button
                type="submit"
                disabled={isSubmitting}
                className="px-4 py-2 bg-bceao-secondary text-bceao-primary rounded-md hover:bg-yellow-500 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {isSubmitting ? 'Mise à jour...' : 'Mettre à jour'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default UpdateClient;
