import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ClientSearchBar = ({ onSearch }) => {
  const [searchParams, setSearchParams] = useState({
    imageFace: null,
    firstName: '',
    lastName: ''
  });
  const [previewImage, setPreviewImage] = useState(null);
  const [searchResult, setSearchResult] = useState(null);

  const handleChange = async (e) => {
    const { name, files } = e.target;
    if (name === 'imageFace') {
      setSearchParams((prev) => ({
        ...prev,
        imageFace: files[0]
      }));
      setPreviewImage(URL.createObjectURL(files[0]));

      // Rechercher les clients similaires à partir de l'image de face
      try {
        const formData = new FormData();
        formData.append('image1', files[0]);
        const response = await axios.post('/api/face-recognition', formData);
        setSearchResult(response.data);
      } catch (error) {
        console.error('Erreur lors de la reconnaissance faciale:', error);
        setSearchResult(null);
      }
    } else {
      setSearchParams((prev) => ({
        ...prev,
        [name]: e.target.value
      }));
    }
  };

  // Recherche à chaque changement des paramètres
  useEffect(() => {
    onSearch(searchParams, searchResult);
  }, [searchParams, searchResult, onSearch]);

  return (
    <div className="mb-6 bg-white p-4 rounded-lg shadow">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {/* Recherche par image de face */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Rechercher par image de face
          </label>
          <div className="flex items-center">
            {previewImage && (
              <img
                src={previewImage}
                alt="Preview"
                className="h-12 w-12 rounded-full mr-4 object-cover"
              />
            )}
            <input
              type="file"
              name="imageFace"
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-bceao-primary focus:border-bceao-primary"
            />
          </div>
        </div>
        {/* Recherche par Nom */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Rechercher par Nom
          </label>
          <input
            type="text"
            name="lastName"
            value={searchParams.lastName}
            onChange={handleChange}
            placeholder="Nom de famille"
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-bceao-primary focus:border-bceao-primary"
          />
        </div>
        {/* Recherche par Prénom */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Rechercher par Prénom
          </label>
          <input
            type="text"
            name="firstName"
            value={searchParams.firstName}
            onChange={handleChange}
            placeholder="Prénom"
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-bceao-primary focus:border-bceao-primary"
          />
        </div>
      </div>
    </div>
  );
};

export default ClientSearchBar;
